import React from "react";
import { Modal, ModalBody, ModalFooter, ModalHeader } from "reactstrap";
import ProductForm from "./ProductForm";

export const AddEditModal = ({ isOpen, toggle, selectedProduct, handleChange, handleSubmit, action }) => (
  <Modal isOpen={isOpen} toggle={toggle}>
    <ModalHeader toggle={toggle}>{action === "add" ? "Add Product" : "Edit Product"}</ModalHeader>
    <ModalBody>
      <ProductForm
        selectedProduct={selectedProduct}
        handleChange={handleChange}
        handleSubmit={handleSubmit}
        action={action}
      />
    </ModalBody>
    <ModalFooter>
      <button className="btn btn-secondary" onClick={toggle}>Cancel</button>
    </ModalFooter>
  </Modal>
);

export const DeleteModal = ({ isOpen, toggle, productName, handleDelete }) => (
  <Modal isOpen={isOpen} toggle={toggle}>
    <ModalBody>
      Are you sure you want to delete the product: {productName}?
    </ModalBody>
    <ModalFooter>
      <button className="btn btn-primary" onClick={handleDelete}>Yes</button>
      <button className="btn btn-secondary" onClick={toggle}>No</button>
    </ModalFooter>
  </Modal>
);
