import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import {Modal, ModalBody, ModalFooter, ModalHeader} from 'reactstrap';
import React, { useState, useEffect } from 'react';

function App() {

    const baseUrl="https://localhost:7077/api/products";

    const [data, setData] = useState([]);

    const askGet = async()=>{
      await axios.get(baseUrl).then(response=>{
        setData(response.data);}).catch(error=>{console.log(error);})
      }

      useEffect(()=>{
        askGet();
      })

  return (
    <div className="App">
      <header className="App-header">
        <button className="btn btn-success">Incluir novo produto</button>   
        <table className="table table-bordered">
        <thead>
          <tr>
            <th>Id</th>
            <th>Nome</th>
            <th>Preço</th>
            <th>Descrição</th>
          </tr>
        </thead>
        <tbody>
          {data.map(product=>(
            <tr key={product.id}>
              <td>{product.id}</td>
              <td>{product.name}</td>
            </tr>
          ))}
        </tbody>
      </table>     
      </header>


    </div>
  );
}

export default App;
