import React, { useState, useEffect } from "react";
import axios from "axios";
import ProductList from "./components/ProductList";
import { AddEditModal, DeleteModal } from "./components/Modals";

function App() {
  const baseUrl = "https://localhost:7077/api/products";
  const [data, setData] = useState([]);
  const [updateData, setUpdateData] = useState(true);
  const [selectedProduct, setSelectedProduct] = useState({});
  const [modalInclude, setModalInclude] = useState(false);
  const [modalEdit, setModalEdit] = useState(false);
  const [modalDelete, setModalDelete] = useState(false);

  const fetchProducts = async () => {
    try {
      const response = await axios.get(baseUrl);
      setData(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    if (updateData) {
      fetchProducts();
      setUpdateData(false);
    }
  }, [updateData]);

  const openModalInclude = () => setModalInclude(!modalInclude);
  const openModalEdit = (product) => {
    setSelectedProduct(product);
    setModalEdit(!modalEdit);
  };
  const openModalDelete = (product) => {
    setSelectedProduct(product);
    setModalDelete(!modalDelete);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setSelectedProduct((prevState) => ({ ...prevState, [name]: value }));
  };

  const handleAdd = async () => {
    try {
      await axios.post(baseUrl, selectedProduct);
      setUpdateData(true);
      openModalInclude();
    } catch (error) {
      console.error(error);
    }
  };

  const handleEdit = async () => {
    try {
      await axios.put(`${baseUrl}/${selectedProduct.id}`, selectedProduct);
      setUpdateData(true);
      openModalEdit();
    } catch (error) {
      console.error(error);
    }
  };

  const handleDelete = async () => {
    try {
      await axios.delete(`${baseUrl}/${selectedProduct.id}`);
      setUpdateData(true);
      openModalDelete();
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="App">
      <button className="btn btn-success" onClick={openModalInclude}>Add Product</button>
      <ProductList data={data} openModalEdit={openModalEdit} openModalDelete={openModalDelete} />

      <AddEditModal
        isOpen={modalInclude || modalEdit}
        toggle={modalInclude ? openModalInclude : openModalEdit}
        selectedProduct={selectedProduct}
        handleChange={handleChange}
        handleSubmit={modalInclude ? handleAdd : handleEdit}
        action={modalInclude ? "add" : "edit"}
      />

      <DeleteModal
        isOpen={modalDelete}
        toggle={openModalDelete}
        productName={selectedProduct.name}
        handleDelete={handleDelete}
      />
    </div>
  );
}

export default App;
