import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import { Modal, ModalBody, ModalFooter, ModalHeader } from "reactstrap";
import React, { useState, useEffect } from "react";

function App() {
  const baseUrl = "https://localhost:7077/api/products";
  const [data, setData] = useState([]);

  const askGet = async () => {
    await axios
      .get(baseUrl)
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };
  const askPost = async () => {
    delete selectedProduct.id;
    selectedProduct.price = parseFloat(selectedProduct.price);
    await axios
      .post(baseUrl, selectedProduct)

      .then((response) => {
        setData(data.concat(response.data));
        openModalInclude();
      })
      .catch((error) => {
        console.log(error);
      });
  };
  const askPut = async () => {
    selectedProduct.price = parseFloat(selectedProduct.price);
    await axios
      .put(baseUrl + "/" + selectedProduct.id, selectedProduct)
      .then((response) => {
        var answer = response.data;
        var auxiliarData = data;

        auxiliarData.forEach((product) => {
          if (product.id === selectedProduct.id) {
            product.name = answer.name;
            product.price = answer.price;
            product.description = answer.description;
          }
        });
        openModalEdit();
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setSelectedProduct({
      ...selectedProduct,
      [name]: value,
    });
    console.log(selectedProduct);
  };

  useEffect(() => {
    askGet();
  });

  const [selectedProduct, setSelectedProduct] = useState({
    id: "",
    name: "",
    price: "",
    description: "",
  });
  const [modalInclude, setModalInclude] = useState(false);
  const openModalInclude = () => {
    setModalInclude(!modalInclude);
  };
  const [modalEdit, setModalEdit] = useState(false);
  const openModalEdit = () => {
    setModalEdit(!modalEdit);
  };
  const [modalDelete, setModalDelete] = useState(false);
  const openModalDelete = () => {
    setModalDelete(!modalDelete)
  }

  const selectProduct = (product, option) => {
    setSelectedProduct(product);
    (option === "Editar") ? openModalEdit(): openModalDelete();
  }

  return (
    <div className="App">
      <header>
        <button className="btn btn-success" onClick={() => openModalInclude()}>
          Incluir novo produto
        </button>
      </header>

      <table className="table table-bordered">
        <thead>
          <tr>
            <th>Id</th>
            <th>Nome</th>
            <th>Preço</th>
            <th>Descrição</th>
            <th>Opções</th>
          </tr>
        </thead>

        <tbody>
          {data.map((product) => (
            <tr key={product.id}>
              <td>{product.id}</td>
              <td>{product.name}</td>
              <td>{product.price}</td>
              <td>{product.description}</td>
              <td>
                <button
                  className="btn btn-primary"
                  onClick={() => selectProduct(product, "Editar")}
                >
                  Editar
                </button>
                &nbsp;
                <button
                  className="btn btn-danger"
                  onClick={() => selectProduct(product, "Excluir")}
                >
                  Excluir
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <Modal isOpen={modalInclude}>
        <ModalHeader>Incluir produto</ModalHeader>
        <ModalBody>
          <div className="form-group">
            <label>Nome: </label>
            <br />
            <input
              type="text"
              className="form-control"
              name="name"
              onChange={handleChange}
            />
            <label>Preço: </label>
            <br />
            <input
              type="text"
              className="form-control"
              name="price"
              onChange={handleChange}
            />
            <label>Descrição: </label>
            <br />
            <input
              type="text"
              className="form-control"
              name="description"
              onChange={handleChange}
            />
          </div>
        </ModalBody>
        <ModalFooter>
          <button className="btn btn-primary" onClick={() => askPost()}>
            Incluir
          </button>
          {"   "} &nbsp;
          <button className="btn btn-danger" onClick={() => openModalInclude()}>
            Cancelar
          </button>
        </ModalFooter>
      </Modal>
      <Modal isOpen={modalEdit}>
        <ModalHeader>Editar produto</ModalHeader>
        <ModalBody>
          <div className="form-group">
            <label>ID: </label>
            <br /> <readOnly value={selectedProduct && selectedProduct.id} />
            <br />
            <label>Nome: </label>
            <br />
            <input
              type="text"
              className="form-control"
              name="name"
              onChange={handleChange}
              value={selectedProduct && selectedProduct.name}
            />
            <label>Preço: </label>
            <br />
            <input
              type="text"
              className="form-control"
              name="price"
              onChange={handleChange}
              value={selectedProduct && selectedProduct.price}
            />
            <label>Descrição: </label>
            <br />
            <input
              type="text"
              className="form-control"
              name="description"
              onChange={handleChange}
              value={selectedProduct && selectedProduct.description}
            />
          </div>
        </ModalBody>
        <ModalFooter>
          <button className="btn btn-primary" onClick={() => askPut()}>
            Editar
          </button>
          &nbsp;
          <button className="btn btn-danger" onClick={() => openModalEdit()}>
            Cancelar
          </button>
        </ModalFooter>
      </Modal>
      <Modal>
        <ModalBody isOpen={modalDelete}>
          Deseja excluir o produto: {selectedProduct && selectedProduct.name} ?
        </ModalBody>
        <ModalFooter>
          <button className="btn btn-primary" onClick={() => askDelete()}>Sim</button>
          <button className="btn btn-secondary" onClick={() => openModalDelete()}>Não</button>
        </ModalFooter>
      </Modal>
    </div>
  );
}

export default App;
