import React from "react";

export const ProductForm = ({ selectedProduct, handleChange, handleSubmit, action }) => {
  return (
    <div className="form-group">
      <label>ID: </label>
      <br />
      <input type="text" className="form-control" readOnly value={selectedProduct.id || ""} />
      <label>Name: </label>
      <br />
      <input type="text" className="form-control" name="name" onChange={handleChange} value={selectedProduct.name || ""} />
      <label>Price: </label>
      <br />
      <input type="text" className="form-control" name="price" onChange={handleChange} value={selectedProduct.price || ""} />
      <label>Description: </label>
      <br />
      <input type="text" className="form-control" name="description" onChange={handleChange} value={selectedProduct.description || ""} />
      <br />
      <button className="btn btn-primary" onClick={handleSubmit}>
        {action === "add" ? "Add Product" : "Edit Product"}
      </button>
    </div>
  );
};
