function ShoppingCart({getTotalProducts, cart, removeFromCart}) {
  return (
    <div>
      <h2>Shopping Cart</h2>
      <h4>Total: {getTotalProducts()}</h4>
      <ul>
        {cart.map((product, index) => (
          <li key={index}>
            {product.name} - R${product.price}
            <button onClick={() => removeFromCart(product.id)}>Remover</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
export default ShoppingCart;
