import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import {Modal, ModalBody, ModalFooter, ModalHeader} from 'reactstrap';
import React, { useState, useEffect } from 'react';

function App() {

    const baseUrl="https://localhost:7077/api/products";

    const [data, setData] = useState([]);

    const askGet = async()=>{
      await axios.get(baseUrl).then(response=>{
        setData(response.data);}).catch(error=>{console.log(error);})
      }

      useEffect(()=>{
        askGet();
      })

  return (
    <div className="App">

      <header>
        <button className="btn btn-success">Incluir novo produto</button>
      </header>

      <table className="table table-bordered">
        <thead>
          <tr>
            <th>Id</th>
            <th>Nome</th>
            <th>Preço</th>
            <th>Descrição</th>
            <th>Opções</th>
          </tr>
        </thead>

        <tbody>
          {data.map(product=>(
            <tr key={product.id}>
              <td>{product.id}</td>
              <td>{product.name}</td>
              <td>{product.price}</td>
              <td>{product.description}</td>
              <td>
                <button className="btn btn-primary">Editar</button>&nbsp;
                <button className="btn btn-danger">Excluir</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>  

       <Modal>
        <ModalHeader>Incluir produto</ModalHeader>
        <ModalBody>
          <div className="form-group">
            <label>Nome: </label>
            <br/>
            <input type="text" className="form-control"/>
            <label>Preço: </label>
            <br/>
            <input type="text" className="form-control"/>
            <label>Descrição: </label>
            <br/>
            <input type="text" className="form-control"/>
          </div>
        </ModalBody>
        </Modal>   
    </div>
  );
}

export default App;
