import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import { Modal, ModalBody, ModalFooter, ModalHeader } from "reactstrap";
import React, { useState, useEffect } from "react";
import ProductList from "./components/ProductList.js";

function App() {
  const baseUrl = "https://localhost:7077/api/products";

  const [data, setData] = useState([]);
  const [updateData, setUpdateData] = useState(true);
  const [selectedProduct, setSelectedProduct] = useState({
    id: "",
    name: "",
    price: "",
    description: "",
  });
  const [searchValue, setSearchValue] = useState("");

  useEffect(() => {
    if (updateData) {
      askGet();
      setUpdateData(false);
    }
  }, [updateData]);

  const askGet = async () => {
    await axios
      .get(baseUrl)
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };
  const askGetByName = async (e) => {
    setSearchValue(e.target.value);

    if(searchValue.trim() === ""){
      setUpdateData(true);
    }
    await axios.get(baseUrl + "/ProductByName?name=" + searchValue).then((response) => {
      setData(response.data);
    })
    .catch((error) => {
      console.log(error);
    });
  }
  const askPost = async () => {
    delete selectedProduct.id;
    selectedProduct.price = parseFloat(selectedProduct.price);
    await axios
      .post(baseUrl, selectedProduct)

      .then((response) => {
        setData(data.concat(response.data));
        setUpdateData(true);
        openModalInclude();
      })
      .catch((error) => {
        console.log(error);
      });
  };
  const askPut = async () => {
    selectedProduct.price = parseFloat(selectedProduct.price);
    await axios
      .put(baseUrl + "/" + selectedProduct.id, selectedProduct)
      .then((response) => {
        var answer = response.data;
        var auxiliarData = data;

        auxiliarData.forEach((product) => {
          if (product.id === selectedProduct.id) {
            product.name = answer.name;
            product.price = answer.price;
            product.description = answer.description;
          }
        });
        setUpdateData(true);
        openModalEdit();
      })
      .catch((error) => {
        console.log(error);
      });
  };
  const askDelete = async () => {
    await axios
      .delete(baseUrl + "/" + selectedProduct.id)
      .then((response) => {
        setData(data.filter((product) => product.id === response.data));
        setUpdateData(true);
        openModalDelete();
      })
      .catch((error) => {
        console.log(error);
      });
  };
  const handleChange = (e) => {
    const { name, value } = e.target;
    setSelectedProduct({
      ...selectedProduct,
      [name]: value,
    });
    console.log(selectedProduct);
  };

  const [modalInclude, setModalInclude] = useState(false);
  const openModalInclude = () => {
    setModalInclude(!modalInclude);
  };
  const [modalEdit, setModalEdit] = useState(false);
  const openModalEdit = () => {
    setModalEdit(!modalEdit);
  };
  const [modalDelete, setModalDelete] = useState(false);
  const openModalDelete = () => {
    setModalDelete(!modalDelete);
  };
  const selectProduct = (product, option) => {
    setSelectedProduct(product);
    if (option === "Editar") {
      openModalEdit();
    } else {
      openModalDelete();
    }
  };

  return (
    <div className="App">
      <div className="header">
        <button className="btn btn-success" onClick={() => openModalInclude()}>
          Novo produto
        </button> &nbsp; &nbsp;     
        <input
        placeholder="Procure um produto..."
              type="text"
              className="form-control"
              value={searchValue}
              onKeyUp={askGetByName}
              onInput={askGetByName}
            />
      </div>
      <ProductList selectProduct={selectProduct} data={data} />

      <Modal isOpen={modalInclude}>
        <ModalHeader>Incluir produto</ModalHeader>
        <ModalBody>
          <div className="form-group">
            <label>Nome: </label>
            <br />
            <input
              type="text"
              className="form-control"
              name="name"
              onChange={handleChange}
            />
            <label>Preço: </label>
            <br />
            <input
              type="text"
              className="form-control"
              name="price"
              onChange={handleChange}
            />
            <label>Descrição: </label>
            <br />
            <input
              type="text"
              className="form-control"
              name="description"
              onChange={handleChange}
            />
          </div>
        </ModalBody>
        <ModalFooter>
          <button className="btn btn-primary" onClick={() => askPost()}>
            Incluir
          </button>
          {"   "} &nbsp;
          <button className="btn btn-danger" onClick={() => openModalInclude()}>
            Cancelar
          </button>
        </ModalFooter>
      </Modal>
      <Modal isOpen={modalEdit}>
        <ModalHeader>Editar produto</ModalHeader>
        <ModalBody>
          <div className="form-group">
            <label>ID: </label>
            <br /> <input               type="text"
              className="form-control" readOnly value={selectedProduct && selectedProduct.id} />
            <label>Nome: </label>
            <br />
            <input
              type="text"
              className="form-control"
              name="name"
              onChange={handleChange}
              value={selectedProduct && selectedProduct.name}
            />
            <label>Preço: </label>
            <br />
            <input
              type="text"
              className="form-control"
              name="price"
              onChange={handleChange}
              value={selectedProduct && selectedProduct.price}
            />
            <label>Descrição: </label>
            <br />
            <input
              type="text"
              className="form-control"
              name="description"
              onChange={handleChange}
              value={selectedProduct && selectedProduct.description}
            />
          </div>
        </ModalBody>
        <ModalFooter>
          <button className="btn btn-primary" onClick={() => askPut()}>
            Editar
          </button>
          &nbsp;
          <button className="btn btn-danger" onClick={() => openModalEdit()}>
            Cancelar
          </button>
        </ModalFooter>
      </Modal>
      <Modal isOpen={modalDelete}>
        <ModalBody>
          Deseja excluir o produto: {selectedProduct && selectedProduct.name} ?
        </ModalBody>
        <ModalFooter>
          <button className="btn btn-primary" onClick={() => askDelete()}>
            Sim
          </button>
          <button
            className="btn btn-secondary"
            onClick={() => openModalDelete()}
          >
            Não
          </button>
        </ModalFooter>
      </Modal>
    </div>
  );
}

export default App;
